import React, {Fragment} from 'react'
import Cookies from 'universal-cookie'
import './TaskList.sass'

const cookies = new Cookies()

const createTaskList = () => {
    let tasks = cookies.get('tasks') || []
    let prior = ''
    if (tasks.length === 0) return 'No tasks!'
    else 
    {
        tasks = tasks.map( task => {
            if (task.priority === 1) prior = '<span className=".priorityCircleGrey></span>"';
            else if (task.priority === 2) prior = '<span className=".priorityCircleBlue></span>"';
            else prior = '<span className=".priorityCircleRed></span>"';
            <div>
                {prior}{task.title} 
                <br/>Deadline: {task.date}
            </div>
        })
        return tasks
    }
}

const TaskList = () => {
    return (
        <Fragment>
            <div className='taskListPanel'>
                <span className='taskListHeader'>These are your current tasks: </span>
                <div>{createTaskList()}</div>
            </div>
        </Fragment>
    )
}

export default TaskList
