import React, {Component, Fragment} from 'react'
import Cookies from 'universal-cookie'
import './TaskList.sass'
import done from './img/done.png'
import notasks from './img/logo.png'

const cookies = new Cookies()

const taskDone = props => {
    let tasks = cookies.get('tasks')
    tasks.map(task => {
        if (task.title === props) {
            task.done = true
            cookies.set('tasks', tasks)
        }
    })
    window.location.reload()
}

const taskDelete = props => {
    let tasks = cookies.get('tasks')
    tasks = tasks.filter(task => 
      task.title !== props
    )
    cookies.set('tasks', tasks)
    window.location.reload()
}

const choosePrior = prior => {
    if (prior === 1) return 'priorityCircleGrey'
    else if (prior === 2) return 'priorityCircleBlue'
    else return 'priorityCircleRed'
}

const createDate = date => {
    const day = new Date(date).getDate()
    const month = new Date(date).getMonth()
    const year = new Date(date).getFullYear()
    return day + '/' + (month+1) + '/' + year
}

const handleTask = () => {
    document.querySelector('.taskMenu').style.display = 'block'
}

const taskClose = () => {
    document.querySelector('.taskMenu').style.display = 'none'
}

const createTaskList = () => {
    let tasks = cookies.get('tasks') || []
    if (tasks.length === 0) return <img src={notasks} alt='no-tasks' className='noTasks' />

    else 
    {
        tasks = tasks.sort(function(a,b) {
            let t1 = new Date(a.date).getTime()
            let t2 = new Date(b.date).getTime()
            return t1-t2
        })

        tasks = tasks.map(task => 
            <Fragment key={task.title}>
                <div className={task.done ? 'taskListDone': 'taskListToDo'}>
                    <span className={choosePrior(task.priority)}></span>
                    <span onClick={handleTask.bind(this, task.title)} className='taskName'>{task.title} </span><span className='taskDate'>{createDate(task.date)}</span>
                </div>
            </Fragment>
        )
        return tasks
    }
}

class TaskList extends Component {
    state = {
        task: ''
    }

    render() {
        return (
            <Fragment>
                <div className='taskListPanel'>
                    <span className='taskListHeader'>These are your current tasks: </span>
                    <div className='tasksListMobile'>{createTaskList()}</div>
                </div>
                <div className='taskMenu'>
                    <div className='taskOpt' /* onClick={task.done ? '' : taskDone.bind(this, task.title)} */>Done</div>
                    <div className='taskOpt' /* onClick={taskDelete.bind(this, task.title)} */>Delete</div>
                    <div className='taskOpt' onClick={taskClose}>Cancel </div>
                </div>
            </Fragment>
        )
    }
}

export default TaskList
