import React, {Component, Fragment} from 'react'
import './TaskAdd.sass'

class TaskAdd extends Component {
    state = {
        title: '',
    }

    handleTitleChange = e => {
        this.setState({
            title: e.target.value
        })
    }

    render () {
    return (
        <Fragment>
            <div className='taskAddPanel'>
                <div className='userHeader'>Hey!</div>
                <div className='newTaskForm'>
                    <span className='newTaskHeader'>Add new task</span>
                    <input value={this.state.title} onChange={this.handleTitleChange} placeholder='title' type='text' />
                    <input value={this.state.date} onChange={this.handleDateChange} placeholder='date' type='date' />
                    <div className='priorityChoose'>
                    <span>Priority:</span>
                        <span className='priorityOption'>Low</span>
                        <span className='priorityOption'>Medium</span>
                        <span className='priorityOption'>High</span>
                    </div>
                    <div className='addTask'></div>
                </div>
                <div className='login'>
                    <span className='login-title'>Welcome back.</span>
                    <span className='login-subtitle'>It is really nice to see you again!</span>
                    <input value={this.state.email} onChange={this.handleEmailChange} placeholder='email' type='text' />
                    <input value={this.state.pass} onChange={this.handlePassChange} placeholder='password' type='password' />
                </div>
            </div>
        </Fragment>
    )}
}

export default TaskAdd
