import React, {Component, Fragment} from 'react'
import add from './img/add.png'
import './TaskAdd.sass'

class TaskAdd extends Component {
    state = {
        title: '',
    }

    handleTitleChange = e => {
        this.setState({
            title: e.target.value
        })
    }

    render () {
    return (
        <Fragment>
            <div className='taskAddPanel'>
                <div className='userHeader'>Hey!</div>
                <div className='newTaskForm'>
                    <span className='newTaskHeader'>Add new task</span>
                    <input value={this.state.title} onChange={this.handleTitleChange} placeholder='title' type='text' />
                    <input value={this.state.date} onChange={this.handleDateChange} placeholder='date' type='date' />
                    <div className='priorityChoose'>
                    <span>Priority:</span>
                        <span className='priorityOption'>Low</span>
                        <span className='priorityOption'>Medium</span>
                        <span className='priorityOption'>High</span>
                    </div>
                    <div className='addTask'><img src={add} alt='add' /></div>
                </div>
            </div>
        </Fragment>
    )}
}

export default TaskAdd
